mapper.select:
select lastSq.*, d.name from designer d, (select outSq.*, rownum rn from (select inSq.con_no, dc.cour_no, dc.price, dc.title, inSq.title as review_title, inSq.contents, inSq.rate, inSq.mem_id, inSq.des_id, to_char(wdate, 'YYYY-MM-DD') as stringwDate from DES_COURSE dc, (select ch.con_no, ch.price, ch.cour_no, r.contents, r.title, r.rate, r.mem_id, r.des_id, wdate from consulting_history ch, review r where ch.con_no = r.con_no and r.state = 'Y' and des_id = #{desId}) inSq where inSq.cour_no = dc.cour_no
order by wdate desc) outSq
order by rate desc) outSq
order by rate asc) outSq
) lastSq where d.id = lastSq.des_id and rn between #{firstCnt} and #{lastCnt}
